// react 
import { useState, useEffect } from 'react'

// css
import '../css/App.css';
import '../media/icons/css/uicons-regular-rounded.css';

// components
import Header from "./Header.js"
import Intro from "./Intro.js"
import About from "./About.js"
import Projects from "./projects/Projects.js"

// animation
import { AnimatePresence } from 'framer-motion';

const App = () => {

  const [ header, setHeader ] = useState('');

  const showAboutHeader = ( bool ) => {
    setHeader('About');
  }

  const showProjectsHeader = ( bool ) => {
    setHeader('Projects');
  }

  return (
    <div className="App">
      <AnimatePresence>
        {(!!renderHeader)? <Header key='header' />:null}
      </AnimatePresence>
      <Intro renderHeader={showAboutHeader} />
      <About renderHeader={showProjectsHeader} />
      <Projects />
    </div>
  );
}

export default App;
