                setBodyText(dataBody);
                break;
            case('smartphone'):
                break;
            case('lock'):
                break;
            case('upload'):
                break;
        }
    }, [activeIcon])

    return(
        <div className='About align-center full-screen'>
            <div className={`about-container ${activeIcon}`}>

                <h2> {headText} </h2>

                    {aboutBody}

                    <AboutBody activeIcon={activeIcon} />

                    {/* <p className='about-body' > {bodyText} </p> */}

            </div>
            <IconInfoBar setActiveIcon={setIcon} />
        </div>
    );
}

export default About;
