import { AnimatePresence, motion } from "framer-motion";
import { useEffect, useState } from "react";

import ghIcon from "../../media/GitHubIcon.png"


const Project = (props) => {

    // props -  {icon}
    //          {title}
    //          {descr}
    //          {link}
    //          {ghLink}

    const [isHovering, setIsHovering] = useState(false)
    const [showDescr, setShowDescr] = useState(false)



    useEffect(() => {
        if(isHovering === false) {
            setShowDescr(false)
        }
    }, [isHovering])

    return (
        <div className='Project' style={(showDescr === true)? {height:'calc(var(--project-width))'}:null} onMouseOver={() => {setIsHovering(true)}} onMouseLeave={() => {setIsHovering(false)}} >


            <AnimatePresence>
            {(isHovering === true) ? 
                <motion.div className={`project-description-container ${props.title}`} key={`project-description-container ${props.title}`} initial={{opacity:0}} animate={{opacity:1}} exit={{opacity:0}} transition={{duration: .4}}  >
                    <h3 className='project-title'>{props.title}</h3>
                    <span className='project-description' >{props.descr}</span>
                    {(showDescr) ? 
                        <ul className='project-list'>
                            {(props.list)?(props.list.map((l, i) => <li key={i}>{l}</li>)):null}
                        </ul>
                    : null }
                    <motion.span className='project-link-container' initial={{opacity:0}} animate={{opacity:1}} exit={{opacity:0}}>
                        <a href={props.ghLink} className='github-link'> <img className='github-icon' src={ghIcon}/> </a>
                        <a href={props.link} className='project-link'>View Site</a>
                    </motion.span>

                    <button onClick={() => {setShowDescr(!showDescr)}}> {(showDescr === false)?<i className='fi-rr-angle-small-down'/>:<i className='fi-rr-angle-small-up'/>}</button>
                </motion.div>
                :
                <motion.img key='project-image' initial={{scale:1.2}} animate={{scale:1}} exit={{scale:1.2}} transition={{duration: .5}} className='project-image' src={props.icon} />
            }
            </AnimatePresence>



        </div>
    );
} 

export default Project;
