import { AnimatePresence, motion } from "framer-motion";
import { useState } from "react";


const Project = (props) => {

    // props -  {icon}
    //          {descr}
    //          {link}
    //          {ghLink}

    const [isHovering, setIsHovering] = useState(false)

    return (
        <div className='Project' onMouseOver={() => {setIsHovering(true)}} onMouseLeave={() => {setIsHovering(false)}} >
            <AnimatePresence>
            {(isHovering === true) ? 
                <motion.span key='project-description' initial={{opacity:0}} animate={{opacity:1}} exit={{opacity:0}} transition={{duration: .2}} className='project-description' >{props.descr}</motion.span>
                :
                <motion.img key='project-image' initial={{scale:0}} animate={{scale:1}} exit={{scale:0}} transition={{duration: .2}} className='project-image' src={props.icon} />
            }
            </AnimatePresence>

        </div>
    );
} 

export default Project;
