import { AnimatePresence, motion } from "framer-motion";
import { useEffect, useState } from "react";


const Project = (props) => {

    // props -  {icon}
    //          {title}
    //          {descr}
    //          {link}
    //          {ghLink}

    const [isHovering, setIsHovering] = useState(false)
    const [showDescr, setShowDescr] = useState(false)

    useEffect(() => {
        if(isHovering === false) {
            setShowDescr(false)
        }
    }, [isHovering])

    return (
        <div className='Project' style={(showDescr === true)? {height:'500px'}:null} onMouseOver={() => {setIsHovering(true)}} onMouseLeave={() => {setIsHovering(false)}} >
            <AnimatePresence>
            {(isHovering === true) ? 
                <motion.div className={`project-description-container ${props.title}`} key={`project-description-container ${props.title}`} initial={{opacity:0}} animate={{opacity:1}} exit={{opacity:0}} transition={{duration: .4}}  >
                    <h3 className='project-title'>{props.title}</h3>
                    <span className='project-description' >{props.descr}</span>
                    <button onClick={() => {setShowDescr(true)}}>read more</button>
                </motion.div>
                :
                <motion.img key='project-image' initial={{scale:1.2}} animate={{scale:1}} exit={{scale:1.2}} transition={{duration: .5}} className='project-image' src={props.icon} />
            }
            </AnimatePresence>

        </div>
    );
} 

export default Project;
