// react
import { useState } from "react";

// components
import IconInfoBar from "./IconBar/IconInfoBar.js"

const About = ( props ) => {

    // props : beginAnim ( bool )

    const [ activeIcon, setActiveIcon ] = useState('user');

    return(
        <div className='About align-center full-screen'>
            <div className='about-container'>

                <h2> Hello, I'm Josiah Eakle, </h2>
                <p className='about-body' > I help small buisnesses and individuals design, develop, and deploy web applications. 
                    Proficient in code, I am driven to create appealing, efficient, and secure software.  </p>

                <IconInfoBar setActiveIcon={setActiveIcon} />

            </div>
        </div>
    );
}

export default About;
