import { AnimatePresence, motion } from "framer-motion";
import { useState } from "react";


const Project = (props) => {

    // props -  {icon}
    //          {descr}
    //          {link}
    //          {ghLink}

    const [isHovering, setIsHovering] = useState(false)

    return (
        <div className='Project' onMouseOver={() => {setIsHovering(true)}} onMouseLeave={() => {setIsHovering(false)}} >
            <AnimatePresence>
            {(!!isHovering) ? 
                <span>{props.descr}</span>
            :
                <motion.img  className='project-image' src={props.icon} />
            }
            </AnimatePresence>

        </div>
    );
} 

export default Project;
