/*

    HEADER

        - Logo Image {input through props if wanted}
        - Site Name

        - Scroll down for the header to shrink
        - Header will animate 

*/

// react
import { useState, useEffect } from "react"

// anim
import { motion, AnimatePresence } from "framer-motion"

const Header = () => {

    useEffect(() => {
        console.log(`mounted`)
        return(() => {
            console.log('unmounted')
        })
    }, [])

    return (
        <AnimatePresence>
            <motion.div key='header' initial={{scale:0.5, opacity: 0}} exit={{scale:0.5, opacity: 0}} animate={{scale:1, opacity: 1}} className={`Header`} >
                <h1> Josiah Eakle Full-Stack Developer </h1>
            </motion.div>
        </AnimatePresence>
    );
}

export default Header;
