// components
import BarIcon from "./BarIcon.js"

import {useState} from 'react'

const IconInfoBar = ( props ) => {

    // props : { setActiveIcon (['me', 'front-end', 'back-end', 'mobile', 'secure', 'hosting']) }
    //              switches text on about page

    const [activeIcon, setActiveIcon] = useState('me')

    /*

    Front-End
        With an understanding of
        HTML
        CSS
        JavaScript
        React.js
        I push to create beautiful and functional websites, designed to be sharp and streamlined for all.
    Back-End
        Any user authenticaion or data managment will inevitably require a back-end. Experienced in 
        Node.js, Express.js, and PHP,
        I will deliver a secure back-end or REST API perfect for your use case.  
    Mobile Responsive
        Everyone owns a smartphone, my designs keep this in mind to provide clean experiences for both mobile and desktop users. 


    Secure

    Hosting and Management

    */

    return(
        <div className='IconInfoBar'>
            <BarIcon iconName='user' iconActive={(activeIcon === 'me')?true:false} />
            <BarIcon iconName='computer' iconActive={(activeIcon === 'front-end')?true:false} />
            <BarIcon iconName='database' iconActive={(activeIcon === 'back-end')?true:false} />
            <BarIcon iconName='smartphone' iconActive={(activeIcon === 'mobile')?true:false} />
            <BarIcon iconName='lock' iconActive={(activeIcon === 'secure')?true:false} />
            <BarIcon iconName='upload' iconActive={(activeIcon === 'hosting')?true:false} />
        </div>
    );
}

export default IconInfoBar;
