/*

    HEADER

        - Logo Image {input through props if wanted}
        - Site Name

        - Scroll down for the header to shrink
        - Header will animate 

*/

// react
import { useState, useEffect } from "react"

// anim
import { motion, useAnimation } from "framer-motion"

const Header = () => {

    const controls = useAnimation();

    return (
        <motion.div initial={{opacity: 0, scale: 0.5}} animate={controls} transition={{duration: 0.5}} exit={{opacity:0, scale:0.5}} className={`Header` }>
            <h1> Josiah Eakle Full-Stack Developer </h1>
        </motion.div>
    );
}

export default Header;
